/* Ekspresi kondisional dengan Boolean */
public class KasusBoolean {

/**
* @ param args
*/
public static void main(String[] agsr) {
	// TODO Auto-generated method stubs
	/* Kamus */
	boolean bool;
	/* Algoritma */
	bool= true;
	if(bool) {
		System.out.print("true\n");
	} else
		System.out.print("salah\n");
	if(!bool){
		System.out.print("salah\n");
	} else
		System.out.print("benar\n");
}
}